        headers: {
        Authorization: `Bearer ${
            getTokenFromLocalStorage !== null ? getTokenFromLocalStorage : ""
        }`,
        Accept: "application/json",
        User: JSON.parse(localStorage.getItem("customer"))?._id,
        },
    };

    useEffect(() => {
        dispatch(getUserCart(config2));
    }, []);

